import React, { useState, useEffect } from 'react';
import './EventoCadastro.css';

function EventoCadastro({ instituicoes, onClose }) {
  const [eventoNome, setEventoNome] = useState('');
  const [dataInicio, setDataInicio] = useState('');
  const [dataFim, setDataFim] = useState('');
  const [instituicaoSelecionada, setInstituicaoSelecionada] = useState('');
  const [eventos, setEventos] = useState([]);

  // Função para buscar eventos já cadastrados do backend
  const fetchEventos = async () => {
    try {
      const response = await fetch('http://localhost:8080/eventos');
      const data = await response.json();
      setEventos(data);
    } catch (error) {
      console.error('Erro ao buscar eventos:', error);
    }
  };

  useEffect(() => {
    fetchEventos();
  }, []);

  const handleSalvarEvento = async () => {
    if (!eventoNome || !dataInicio || !dataFim || !instituicaoSelecionada) {
      alert('Preencha todos os campos!');
      return;
    }

    const novoEvento = {
      id: 0,  // Para novos registros, o backend deverá atribuir o ID
      nome: eventoNome,
      dataInicial: dataInicio,
      dataFinal: dataFim,
      ativo: true,
      codInstituicao: instituicaoSelecionada,
    };

    try {
      const response = await fetch('http://localhost:8080/eventos', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(novoEvento),
      });
      if (response.ok) {
        const eventoSalvo = await response.json();
        setEventos([...eventos, eventoSalvo]);
        // Limpar campos após salvar
        setEventoNome('');
        setDataInicio('');
        setDataFim('');
        setInstituicaoSelecionada('');
      } else {
        console.error('Erro ao salvar evento');
      }
    } catch (error) {
      console.error('Erro ao conectar com o servidor:', error);
    }
  };

  return (
    <div className="window-container">
      <div className="window-header">
        <span>Cadastro de Eventos</span>
        <button className="close-button" onClick={onClose}>X</button>
      </div>

      <div className="window-body">
        <select
          value={instituicaoSelecionada}
          onChange={(e) => setInstituicaoSelecionada(e.target.value)}
        >
          <option value="">Selecione uma Instituição</option>
          {instituicoes.map((instituicao) => (
            <option key={instituicao.id} value={instituicao.id}>
              {instituicao.nome}
            </option>
          ))}
        </select>

        <input
          type="text"
          value={eventoNome}
          onChange={(e) => setEventoNome(e.target.value)}
          placeholder="Nome do Evento"
        />
        <input
          type="date"
          value={dataInicio}
          onChange={(e) => setDataInicio(e.target.value)}
          placeholder="Data de Início"
        />
        <input
          type="date"
          value={dataFim}
          onChange={(e) => setDataFim(e.target.value)}
          placeholder="Data de Fim"
        />

        <button onClick={handleSalvarEvento}>Salvar Evento</button>

        {/* Grid de Eventos */}
        <div className="evento-lista">
          {eventos.length === 0 ? (
            <div>Nenhum evento cadastrado.</div>
          ) : (
            eventos.map((evento) => (
              <div key={evento.id} className="evento-item">
                <strong>{evento.nome}</strong> - {evento.dataInicial} a {evento.dataFinal}
                <p>Instituição: {instituicoes.find(inst => inst.id === evento.codInstituicao)?.nome || 'N/A'}</p>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
}

export default EventoCadastro;
