import React, { useState } from 'react';
import './EventoCadastro.css';

function EventoCadastro({ instituicao, onClose }) {
  const [eventos, setEventos] = useState([]);
  const [nomeEvento, setNomeEvento] = useState('');
  const [dataEvento, setDataEvento] = useState('');
  const [descricaoEvento, setDescricaoEvento] = useState('');

  const handleSalvarEvento = () => {
    const novoEvento = {
      nome: nomeEvento,
      data: dataEvento,
      descricao: descricaoEvento,
      instituicao,
    };
    setEventos([...eventos, novoEvento]);
    setNomeEvento('');
    setDataEvento('');
    setDescricaoEvento('');
  };

  return (
    <div className="window-container">
      <div className="window-header">
        <span>Cadastro de Eventos - {instituicao}</span>
        <button className="close-button" onClick={onClose}>X</button>
      </div>

      <div className="window-body">
        <input
          type="text"
          value={nomeEvento}
          onChange={(e) => setNomeEvento(e.target.value)}
          placeholder="Nome do Evento"
        />
        <input
          type="date"
          value={dataEvento}
          onChange={(e) => setDataEvento(e.target.value)}
        />
        <textarea
          value={descricaoEvento}
          onChange={(e) => setDescricaoEvento(e.target.value)}
          placeholder="Descrição do Evento"
        />

        <button onClick={handleSalvarEvento}>Salvar Evento</button>

        <div className="evento-lista">
          {eventos.map((evento, index) => (
            <div key={index} className="evento-item">
              <strong>{evento.nome}</strong> - {evento.data}
              <p>{evento.descricao}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default EventoCadastro;
