import React, { useState, useEffect } from 'react';
import './InstituicaoCadastro.css';

function InstituicaoCadastro({ onClose }) {
  const [instituicoes, setInstituicoes] = useState([]);
  const [nomeInstituicao, setNomeInstituicao] = useState('');
  const [tipoInstituicao, setTipoInstituicao] = useState('CONFEDERACAO'); // Valor padrão
  const [isEditing, setIsEditing] = useState(false);
  const [editingInstituicao, setEditingInstituicao] = useState(null);

  // Função para carregar instituições da API
  useEffect(() => {
    const fetchInstituicoes = async () => {
      try {
        const response = await fetch('URL_DA_SUA_API/instituicoes');
        if (!response.ok) throw new Error("Erro ao buscar instituições");
        const data = await response.json();
        setInstituicoes(data); // Assume que a resposta é uma lista de objetos de instituição
      } catch (error) {
        console.error(error);
        alert("Erro ao carregar instituições.");
      }
    };
    fetchInstituicoes();
  }, []);

  const handleIncluir = () => {
    setNomeInstituicao('');
    setTipoInstituicao('CONFEDERACAO'); // Reseta para o valor padrão
    setIsEditing(false);
    setEditingInstituicao(null);
  };

  const handleSalvar = async () => {
    if (!nomeInstituicao.trim()) {
      alert("Por favor, insira o nome da instituição antes de salvar.");
      return;
    }

    try {
      const method = isEditing ? 'PUT' : 'POST';
      const url = isEditing 
        ? `URL_DA_SUA_API/instituicoes/${editingInstituicao.id}`
        : 'URL_DA_SUA_API/instituicoes';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          nome: nomeInstituicao,
          tipoInstituicao: tipoInstituicao,
        })
      });

      if (!response.ok) throw new Error("Erro ao salvar instituição");

      const updatedInstituicao = await response.json();

      if (isEditing) {
        setInstituicoes(instituicoes.map(inst => 
          inst.id === editingInstituicao.id ? updatedInstituicao : inst
        ));
      } else {
        setInstituicoes([...instituicoes, updatedInstituicao]);
      }

      setIsEditing(false);
      setEditingInstituicao(null);
      setNomeInstituicao('');
      setTipoInstituicao('CONFEDERACAO'); // Reseta para o valor padrão
    } catch (error) {
      console.error(error);
      alert("Erro ao salvar a instituição.");
    }
  };

  const handleEditar = (instituicao) => {
    setNomeInstituicao(instituicao.nome);
    setTipoInstituicao(instituicao.tipoInstituicao);
    setIsEditing(true);
    setEditingInstituicao(instituicao);
  };

  const handleExcluir = async (id) => {
    if (window.confirm("Tem certeza que deseja excluir esta instituição?")) {
      try {
        const response = await fetch(`URL_DA_SUA_API/instituicoes/${id}`, {
          method: 'DELETE',
        });

        if (!response.ok) throw new Error("Erro ao excluir instituição");

        setInstituicoes(instituicoes.filter((inst) => inst.id !== id));
      } catch (error) {
        console.error(error);
        alert("Erro ao excluir a instituição.");
      }
    }
  };

  return (
    <div className="instituicao-window-container">
      <div className="instituicao-window-header">
        <span>Cadastro de Instituições</span>
        <button className="instituicao-close-button" onClick={onClose}>X</button>
      </div>

      <div className="instituicao-window-body">
        {/* Grid de Instituições */}
        <div className="instituicao-grid">
          {instituicoes.map((inst) => (
            <div key={inst.id} onClick={() => handleEditar(inst)}>
              {inst.nome} - {inst.tipoInstituicao}
            </div>
          ))}
        </div>

        {/* Campo de Entrada para Nome */}
        <input
          type="text"
          value={nomeInstituicao}
          onChange={(e) => setNomeInstituicao(e.target.value)}
          placeholder="Nome da Instituição"
          className="instituicao-input"
        />

        {/* Campo de Seleção para Tipo de Instituição */}
        <select
          value={tipoInstituicao}
          onChange={(e) => setTipoInstituicao(e.target.value)}
          className="instituicao-select"
        >
          <option value="CONFEDERACAO">Confederação</option>
          <option value="SINGULAR">Singular</option>
          <option value="CENTRAL">Central</option>
          <option value="COOPERATIVA">Cooperativa</option>
        </select>

        {/* Botões de Ação */}
        <div className="instituicao-buttons">
          <button className="instituicao-button" onClick={handleIncluir}>Incluir</button>
          <button
            className="instituicao-button"
            onClick={isEditing ? handleSalvar : () => alert("Selecione uma instituição para editar!")}
          >
            Editar
          </button>
          <button className="instituicao-button" onClick={handleSalvar}>Salvar</button>
          {isEditing && (
            <button
              className="instituicao-button"
              onClick={() => handleExcluir(editingInstituicao.id)}
            >
              Excluir
            </button>
          )}
        </div>
      </div>
    </div>
  );
}

export default InstituicaoCadastro;
