import React, { useState } from 'react';

function InstituicaoCadastro({ onClose }) {
  const [instituicoes, setInstituicoes] = useState([]);
  const [nomeInstituicao, setNomeInstituicao] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editingIndex, setEditingIndex] = useState(null);

  // Função para iniciar uma nova inclusão
  const handleIncluir = () => {
    setNomeInstituicao('');
    setIsEditing(false);
    setEditingIndex(null);
  };

  // Função para salvar (adicionar ou editar) uma instituição
  const handleSalvar = () => {
    if (isEditing) {
      const updatedInstituicoes = [...instituicoes];
      updatedInstituicoes[editingIndex] = nomeInstituicao;
      setInstituicoes(updatedInstituicoes);
      setIsEditing(false);
      setEditingIndex(null);
    } else {
      setInstituicoes([...instituicoes, nomeInstituicao]);
    }
    setNomeInstituicao('');
  };

  // Função para ativar o modo de edição de uma instituição
  const handleEditar = (index) => {
    setNomeInstituicao(instituicoes[index]);
    setIsEditing(true);
    setEditingIndex(index);
  };

  // Função para excluir uma instituição com confirmação
  const handleExcluir = (index) => {
    if (window.confirm("Tem certeza que deseja excluir esta instituição?")) {
      setInstituicoes(instituicoes.filter((_, i) => i !== index));
    }
  };

  return (
    <div className="instituicao-modal">
      <h2>Cadastro de Instituições</h2>

      {/* Grid de Instituições */}
      <div className="instituicao-grid">
        {instituicoes.map((inst, index) => (
          <div key={index} onClick={() => handleEditar(index)}>
            {inst}
          </div>
        ))}
      </div>

      {/* Campo de Entrada */}
      <input
        type="text"
        value={nomeInstituicao}
        onChange={(e) => setNomeInstituicao(e.target.value)}
        placeholder="Nome da Instituição"
      />

      {/* Botões de Ação */}
      <div className="buttons">
        <button onClick={handleIncluir}>Incluir</button>
        <button onClick={isEditing ? handleSalvar : () => alert("Selecione uma instituição para editar!")}>Editar</button>
        <button onClick={handleSalvar}>Salvar</button>
        {isEditing && <button onClick={() => handleExcluir(editingIndex)}>Excluir</button>}
      </div>

      {/* Botão para fechar o modal */}
      <button onClick={onClose} className="close-button">Fechar</button>
    </div>
  );
}

export default InstituicaoCadastro;
