import React, { useState } from 'react';
import './InstituicaoCadastro.css';

function InstituicaoCadastro({ onClose, onAdicionar }) {
  const [instituicoes, setInstituicoes] = useState([]);
  const [nomeInstituicao, setNomeInstituicao] = useState('');

  const handleSalvar = () => {
    if (nomeInstituicao) {
      setInstituicoes([...instituicoes, nomeInstituicao]);
      onAdicionar(nomeInstituicao);
      setNomeInstituicao('');
    }
  };

  return (
    <div className="window-container">
      <div className="window-header">
        <span>Cadastro de Instituições</span>
        <button className="close-button" onClick={onClose}>X</button>
      </div>
      <div className="window-body">
        <input
          type="text"
          value={nomeInstituicao}
          onChange={(e) => setNomeInstituicao(e.target.value)}
          placeholder="Nome da Instituição"
        />
        <button onClick={handleSalvar}>Salvar</button>
      </div>
    </div>
  );
}

export default InstituicaoCadastro;
