import React, { useState, useEffect } from 'react';
import './InstituicaoCadastro.css';

function InstituicaoCadastro({ onClose }) {
  const [instituicoes, setInstituicoes] = useState([]);
  const [nomeInstituicao, setNomeInstituicao] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editingIndex, setEditingIndex] = useState(null);

  useEffect(() => {
    const fetchInstituicoes = async () => {
      try {
        const response = await fetch('URL_DA_SUA_API/instituicoes');
        if (!response.ok) throw new Error("Erro ao buscar instituições");
        const data = await response.json();
        setInstituicoes(data); 
      } catch (error) {
        console.error(error);
        alert("Erro ao carregar instituições.");
      }
    };
    fetchInstituicoes();
  }, []);

  const handleIncluir = () => {
    setNomeInstituicao('');
    setIsEditing(false);
    setEditingIndex(null);
  };

  const handleSalvar = async () => {
    if (!nomeInstituicao.trim()) {
      alert("Por favor, insira o nome da instituição antes de salvar.");
      return;
    }

    try {
      const method = isEditing ? 'PUT' : 'POST';
      const url = isEditing 
        ? `URL_DA_SUA_API/instituicoes/${editingIndex}`
        : 'URL_DA_SUA_API/instituicoes';

      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ nome: nomeInstituicao })
      });

      if (!response.ok) throw new Error("Erro ao salvar instituição");

      const updatedInstituicao = await response.json();

      if (isEditing) {
        const updatedInstituicoes = [...instituicoes];
        updatedInstituicoes[editingIndex] = updatedInstituicao.nome;
        setInstituicoes(updatedInstituicoes);
      } else {
        setInstituicoes([...instituicoes, updatedInstituicao.nome]);
      }

      setIsEditing(false);
      setEditingIndex(null);
      setNomeInstituicao('');
    } catch (error) {
      console.error(error);
      alert("Erro ao salvar a instituição.");
    }
  };

  const handleEditar = (index) => {
    setNomeInstituicao(instituicoes[index]);
    setIsEditing(true);
    setEditingIndex(index);
  };

  const handleExcluir = async (index) => {
    if (window.confirm("Tem certeza que deseja excluir esta instituição?")) {
      try {
        const response = await fetch(`URL_DA_SUA_API/instituicoes/${index}`, {
          method: 'DELETE',
        });

        if (!response.ok) throw new Error("Erro ao excluir instituição");

        setInstituicoes(instituicoes.filter((_, i) => i !== index));
      } catch (error) {
        console.error(error);
        alert("Erro ao excluir a instituição.");
      }
    }
  };

  return (
    <div className="instituicao-window-container">
      <div className="instituicao-window-header">
        <span>Cadastro de Instituições</span>
        <button className="instituicao-close-button" onClick={onClose}>X</button>
      </div>

      <div className="instituicao-window-body">
        {/* Grid de Instituições */}
        <div className="instituicao-grid">
          {instituicoes.map((inst, index) => (
            <div key={index} onClick={() => handleEditar(index)}>
              {inst}
            </div>
          ))}
        </div>

        {/* Campo de Entrada */}
        <input
          type="text"
          value={nomeInstituicao}
          onChange={(e) => setNomeInstituicao(e.target.value)}
          placeholder="Nome da Instituição"
          className="instituicao-input"
        />

        {/* Botões de Ação */}
        <div className="instituicao-buttons">
          <button className="instituicao-button" onClick={handleIncluir}>Incluir</button>
          <button className="instituicao-button" onClick={isEditing ? handleSalvar : () => alert("Selecione uma instituição para editar!")}>Editar</button>
          <button className="instituicao-button" onClick={handleSalvar}>Salvar</button>
          {isEditing && <button className="instituicao-button" onClick={() => handleExcluir(editingIndex)}>Excluir</button>}
        </div>
      </div>
    </div>
  );
}

export default InstituicaoCadastro;
