import React, { useState, useEffect } from 'react';
import './EventoConsulta.css';

function EventoConsulta({ onClose }) {
  const [eventos, setEventos] = useState([]);

  const fetchEventos = async () => {
    try {
      const response = await fetch('http://localhost:8080/eventos');
      const data = await response.json();
      setEventos(data);
    } catch (error) {
      console.error('Erro ao buscar eventos:', error);
    }
  };

  useEffect(() => {
    fetchEventos();
  }, []);

  return (
    <div className="evento-lista">
      {eventos.length === 0 ? (
        <div>Nenhum evento cadastrado.</div>
      ) : (
        eventos.map((evento) => (
          <div
            key={evento.id}
            className={`evento-item ${evento.ativo ? 'ativo' : 'inativo'}`} // Classe dinâmica
          >
            <strong>{evento.nome}</strong> - {evento.dataInicial} a {evento.dataFinal}
            <p>Instituição: {instituicoes.find(inst => inst.id === evento.codInstituicao)?.nome || 'N/A'}</p>
          </div>
        ))
      )}
    </div>
  );
}

export default EventoConsulta;
