import React, { useState } from 'react';
import InstituicaoForm from './InstituicaoForm';
import EventoForm from './EventoForm';

function App() {
  const [instituicoes, setInstituicoes] = useState([]);
  const [eventos, setEventos] = useState({});
  const [instituicaoSelecionada, setInstituicaoSelecionada] = useState(null);

  const adicionarInstituicao = (nomeInstituicao) => {
    setInstituicoes([...instituicoes, nomeInstituicao]);
    setEventos({ ...eventos, [nomeInstituicao]: [] });
  };

  const adicionarEvento = (evento) => {
    setEventos({
      ...eventos,
      [instituicaoSelecionada]: [...eventos[instituicaoSelecionada], evento],
    });
  };

  return (
    <div>
      <h1>Cadastro de Eventos Agendados</h1>
      <InstituicaoForm onSubmit={adicionarInstituicao} />

      <h2>Selecione uma Instituição</h2>
      <select onChange={(e) => setInstituicaoSelecionada(e.target.value)} value={instituicaoSelecionada || ''}>
        <option value="" disabled>Escolha uma instituição</option>
        {instituicoes.map((inst, index) => (
          <option key={index} value={inst}>{inst}</option>
        ))}
      </select>

      {instituicaoSelecionada && (
        <div>
          <h2>Cadastro de Eventos para {instituicaoSelecionada}</h2>
          <EventoForm onSubmit={adicionarEvento} />

          <h3>Eventos Agendados:</h3>
          <ul>
            {eventos[instituicaoSelecionada].map((evento, index) => (
              <li key={index}>
                {evento.descricao} - {evento.dataInicial} até {evento.dataFinal}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
